(()=>{"use strict";(t=>{const e=document.getElementById("timer-days"),n=document.getElementById("timer-hours"),o=document.getElementById("timer-minutes"),r=document.getElementById("timer-seconds"),m=t=>t<=9?"0"+t:t;let s=setInterval((()=>{const t=document.querySelector(".timer-action");let i=(()=>{let t=(new Date("26 februar 2022").getTime()-(new Date).getTime())/1e3;return{timerRemaining:t,days:Math.floor(t/60/60/24),hours:Math.floor(t/60/60%24),minutes:Math.floor(t/60%60),seconds:Math.floor(t%60)}})();e.textContent=m(i.days),n.textContent=m(i.hours),o.textContent=m(i.minutes),r.textContent=m(i.seconds),i.timerRemaining<=0&&(e.textContent="00",n.textContent="00",o.textContent="00",r.textContent="00",t.textContent="Акция закончилась",clearInterval(s))}),1e3)})()})();