(()=>{"use strict";(e=>{const t=document.getElementById("timer-days"),n=document.getElementById("timer-hours"),o=document.getElementById("timer-minutes"),r=document.getElementById("timer-seconds");let m=setInterval((()=>{let e=(()=>{let e=(new Date("24 februar 2022").getTime()-(new Date).getTime())/1e3;return{timerRemaining:e,days:Math.floor(e/60/60/24),hours:Math.floor(e/60/60%24),minutes:Math.floor(e/60%60),seconds:Math.floor(e%60)}})();for(let t in e)e[t]<10&&(e[t]="0"+e[t]);e.timerRemaining>0?(t.textContent=e.days,n.textContent=e.hours,o.textContent=e.minutes,r.textContent=e.seconds):clearInterval(m)}),1e3)})()})();